import React from "react";
import { DivBox, DivName, Img, DivPrice, DivStatus } from './SlotItem.style';
import emptyslot from '../../Images/emptyslot.jpeg';

const emptySlotImgUrl = emptyslot

const SlotItem = ({ product, coin, onPurchase }) => {
  const { productImageUrl, name, price } = product;

  return (
    <DivBox>
      <Img src={productImageUrl || emptySlotImgUrl} />
      <DivName>{name || ""}</DivName>
      <DivPrice>{price || ""}</DivPrice>
      <DivStatus
        onClick={() => {
          if (price > coin) return;
          onPurchase(price);
        }}
        available={name && coin >= price}
      />
    </DivBox>
  );
}
export default SlotItem;
